mapper.update:
update account

    update_time = now(),
    available_balance = #{availableBalance},
    balance = #{balance},
    frozen_balance = #{frozenBalance},
    version = version + 1

where user_id = #{userId}
and card_id = #{cardId}
and currency = #{currency}
and version = #{version}

update account

    update_time = now(),
    status = #{status},
    version = version + 1

where user_id = #{userId}
and card_id = #{cardId}
and currency = #{currency}
and version = #{version}

mapper.select:
select

from account
where user_id = #{userId}
and card_id = #{cardId}
and currency = #{currency}

select

from account
where user_id = #{userId}
and card_id = #{cardId}
